import React, { useState } from "react";
import axios from "axios";
import "./BookingForm.css";

const BookingForm = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [date, setDate] = useState("");
  const [tentType, setTentType] = useState("");

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/bookings", {
        name,
        email,
        phone,
        date,
        tentType,
      });
      console.log("Booking submitted successfully:", response.data);
    } catch (error) {
      console.error("Error submitting booking:", error);
    }
  };

  return (
    <form className="booking-form" onSubmit={handleSubmit}>
      <h2>Book a Tent</h2>
      <div>
        <label htmlFor="name">Name:</label>
        <input
          type="text"
          id="name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
      </div>
      <div>
        <label htmlFor="email">Email:</label>
        <input
          type="email"
          id="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
      </div>
      <div>
        <label htmlFor="phone">Phone:</label>
        <input
          type="tel"
          id="phone"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
          required
        />
      </div>
      <div>
        <label htmlFor="date">Booking Date:</label>
        <input
          type="date"
          id="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          required
        />
      </div>
      <div>
        <label htmlFor="tentType">Tent Type:</label>
        <select
          id="tentType"
          value={tentType}
          onChange={(e) => setTentType(e.target.value)}
          required
        >
          <option value="">Select tent type</option>
          <option value="event">Event Tent</option>
          <option value="camping">Camping Tent</option>
          <option value="luxury">Luxury Tent</option>
        </select>
      </div>
      <button type="submit">Submit</button>
    </form>
  );
};

export default BookingForm;
